import { useState } from "react";
import { CreatePost } from "../api/Requests";

const CreatePostForm = ({token}, {setPosts} , {posts}) => {

    const [postSubmission, setPostSubmission] = useState({});
    const [location, setLocation] = useState("");
    const [willDeliver, setWillDeliver] = useState("false");
    const [price, setPrice] = useState("");
    const [description, setDescription] = useState("");
    const[title, setTitle] = useState("");
    const onSubmitHandler = async (event) => {
        event.preventDefault();
        console.log("onSubmitHandler() called in CreatePostForm");
        setPostSubmission({title: title, location:location,willDeliver:willDeliver, price: price, description:description})
        const {error, post, message} = await CreatePost(token,postSubmission);
        new_posts_value = posts.push(post);
        setPosts(new_posts_value);
    }


    return (<form className="Create-Post-Form" onSubmit={onSubmitHandler}>
        <h1>Create Post Form</h1>

        <div className="field">
            <label>Title</label>
            <input
            type = "text"
            label = "Title"
            placeholder="Title"
            value = {title}
            required
            onChange = {(event) => {setTitle(event.target.value)}}
            />

        </div>

        <div className="field">
            <label>Location</label>
            <input
            type = "text"
            label = "Location"
            placeholder="Location"
            onChange = {(event) => {setLocation(event.target.value)}}
            />

        </div>
        <div className="field">
            <label>Will Deliver</label>
            <input
            type = "checkbox"
            label = "Will Deliver"
            onChange = {() => {setWillDeliver(true)}}
            />
            <input
            type = "checkbox"
            label = "Can't or Won't Deliver"
            onChange = {() => {setWillDeliver(false)}}
            />

        </div>
        <div className="field">
            <label>Price</label>
            <input
            type = "text"
            value = {price}
            placeholder = "Price"
            required
            onChange = {(event) => {setPrice(event.target.value)}}
            />

        </div>
        <div className="field">
            <label>Description</label>
            <input
            type = "text"
            value = {description}
            placeholder = "Description"
            required
            onChange = {(event) => {setDescription(event.target.value)}}
            />

        </div>
        <button className="submit-form" type = "submit">Submit</button>
    </form>);

}

export default CreatePostForm
