const BASEURL = "https://strangers-things.herokuapp.com/api/2207-UNI-WEB-PT";


const MAKEREQUEST = async (method, token ="") => {
    try {
        const data = await fetch(`${BASEURL}/${method}`, {
            method: `${method}`,
            headers: {
              'Content-Type': 'application/json',
              'Authorization': `Bearer ${token}`
            },
            body: JSON.stringify({ /* whatever things you need to send to the API */ })
          }).then(data => {return data})
    }
    catch (error) {
        console.error(error);
    }
}

export default MAKEREQUEST;
