import { useState } from "react";
import { CreatePost } from "../api/Requests";

const CreatePostForm = ({token}) => {

    const [postSubmission, setPostSubmission] = useState({});
    const [location, setLocation] = useState("");
    const [willDeliver, setWillDeliver] = useState("false");
    const [price, setPrice] = useState("");
    const [description, setDescription] = useState("");
    const onSubmitHandler = async (event) => {
        console.log("onSubmitHandler() called in CreatePostForm");
        event.preventDefault();
        const {error, post, message} = await CreatePost(token,postSubmission);

    }


    return (<form className="login-form" onSubmit={onSubmitHandler}>
        <h1>Create Post Form</h1>
        <div className="field">
            <label>Username</label>
            <input
            type = "text"
            value = {location}
            placeholder = "username"
            required
            onChange = {(event) => {setLocation(event.target.value)}}
            />

        </div>
        <div className="field">
            <label>Will Deliver</label>
            <input
            type = "checkbox"
            value = "Will Deliver"
            onChange = {() => {setWillDeliver(true)}}
            />
            <input
            type = "checkbox"
            value = "Can't or Won't Deliver"
            onChange = {() => {setWillDeliver(false)}}
            />

        </div>
        <div className="field">
            <label>Price</label>
            <input
            type = "text"
            value = {location}
            placeholder = "Price"
            required
            onChange = {(event) => {setPrice(event.target.value)}}
            />

        </div>
        <div className="field">
            <label>Description</label>
            <input
            type = "text"
            value = {description}
            placeholder = "Description"
            required
            onChange = {(event) => {setDescription(event.target.value)}}
            />

        </div>
        <button className="submit-form" type = "submit">Submit</button>
    </form>);

}

export default CreatePostForm
