import React, {useState, useEffect} from 'react';
import { DeletePost, SendMessage } from "../api/Requests";

const post = (itemInfo, token, reloadItem, setReloadItem, message, setMessage) => {
    return itemInfo.isAuthor ?  <><div className="post">
    <div className="post-item"> Post: {itemInfo.title}</div>
    <div className="post-item">Author: {itemInfo.author.username} (You)</div>
    <div className="post-item">Price: {itemInfo.price}</div>
    <div className="post-item">Location: {itemInfo.location}</div>
    <div className= "post-item description">Description: {itemInfo.description}</div>
    <div className="post-item messages">Messages: {itemInfo.messages}</div>
    <button className="post-item delete-post" onClick={async() =>{DeletePost(token, itemInfo._id);
    setReloadItem(!reloadItem)}}>Delete Post</button>
</div> </>: <><div className="post">
        <div className="post-item"> Post: {itemInfo.title}</div>
        <div className="post-item">Author: {itemInfo.author.username}</div>
        <div className="post-item">Price: {itemInfo.price}</div>
        <div className="post-item">Location: {itemInfo.location}</div>
        <div className= "post-item description">Description: {itemInfo.description}</div>
        {token ? <form onSubmit = {async () => {SendMessage(token,itemInfo._id, message);
        setReloadItem(!reloadItem);}}>
            <input type = "text" value = {message} onChange = {(event)=> {setMessage(event.target.value)}}></input> <button type = "submit"> Send Message </button> </form>: null}
    </div> </>
    }




const searchForm = (searchTerm, setSearchTerm) => {


  return <input type="text" value = {searchTerm} placeholder = "Enter Search Terms (case sensitive)" onChange = {(event)=> {event.preventDefault();
    setSearchTerm(event.target.value)}}></input>
}

const Posts =  (props) => {


    const [message, setMessage] = useState("");
    const response = props.posts;
    const token = props.token;
    const reloadItem = props.reloadItem;
    const setReloadItem = props.setReloadItem;
    console.log(response);

    const [searchTerm, setSearchTerm] = useState('');


    function postMatches(post, longString) {
        // return true if any of the fields you want to check against include the text
        // strings have an .includes() method 
        const wordArray = longString.split(" ");

        for (let text in wordArray) {
          for (let value in text) {
            const valueLower = value.toLowerCase();
            if (post.title.includes(valueLower)) {
              return true
            }
            if (post.description.includes(valueLower)) {
                return true
              }
            if (post.price.includes(valueLower)) {
            return true
              }
            if (post.author.username.includes(valueLower)) {
            return true
            }
          }

        return false
      }
    }
    const filteredPosts = response.filter(post => postMatches(post, searchTerm));
    return  searchTerm.length ? <><div> {searchForm(searchTerm,setSearchTerm)} </div><div className='posts'> {filteredPosts.map((item) => { return post(item, token, reloadItem, setReloadItem, message, setMessage); })}</div></> : <><div>{searchForm(searchTerm,setSearchTerm)}</div><div className="posts"> {response.map((item) => { return post(item, token, reloadItem, setReloadItem, message, setMessage); })}</div></>


}

export default Posts
