const BASEURL = "https://strangers-things.herokuapp.com/api/2207-UNI-WEB-PT";


const MAKEREQUEST = async (method, token ="") => {
    try {
        await fetch(`${BASEURL}/${method}`, {
            method: `${method}`,
            headers: {
              'Content-Type': 'application/json',
              'Authorization': `Bearer ${token}`
            }

          }).then(data => {return data.json})
    }
    catch (error) {
        console.error(error);
    }
}

export default MAKEREQUEST;
